setKeyValue.value:
return function setKeyValue(){
    //cambiar esta variable por el formOutput
    var p = $data.formOutput;

    var rtn = [];

    for (var key in p) {
        if (p.hasOwnProperty(key)) {
            rtn.push({"clave": key.toString() , "valor": JSON.stringify(p[key])});
        }
    }

    return {"items": rtn} 
}

