function generateLibranza(it , i) {
	var tramaLibranzas = {};
	for(var j = 0; j < $data.filtroProductos.posLibranza.length; j++){
		if($data.filtroProductos.posLibranza[j] === i){
			tramaLibranzas.segmento = {id: $data.filtroProductos.libranzas[j].segmento.id, nombre: $data.filtroProductos.libranzas[j].segmento.nombre};
