			tramaLibranzas.infoConvenio = $data.filtroProductos.libranzas[j].destinoLibranza;
		}
	}
	tramaLibranzas.producto = { nombre: 'LIBRANZAS', id: 3, idCircuito: 3 };
	tramaLibranzas.subProducto = { nombre: 'Libranzas', id: 3 };
	tramaLibranzas.monto = it.montoSolicitado;
	tramaLibranzas.plazo = it.plazoCredito;
	tramaLibranzas.scoring = it.resultadoScoring.relatedContract.contractNumber;
	tramaLibranzas.id = i;
	return tramaLibranzas;
}


