function getAge(dateString) {
   var today = new Date();
   var birthDate_ = new Date(dateString);
   var age = today.getFullYear() - birthDate_.getFullYear();
   var m = today.getMonth() - birthDate_.getMonth();
   if (m < 0 || (m === 0 && today.getDate() < birthDate_.getDate())) {
       age--;
   }
   return age;
}

