consultaProfesor.value:
return function consultaProfesor(){
    var tipo = $data.notificaciones.tipoIdentificacion;
    var numero =$data.notificaciones.numeroIdentificacion;

    for(var i=0; i <= $data.consultaProfesores.length; i++){
        if(tipo===$data.consultaProfesores[i].tipoIdentificacion && numero.toString() ===$data.consultaProfesores[i].numeroIdentificacion){
            var parts =$data.consultaProfesores[i].fechaContrato.split('T',1);
            parts =parts[0].split('-');
            var mydate = new Date(parts[0], parts[1] - 1, parts[2]); 
            $data.notificaciones.fechaContrato   = mydate; 
            $data.notificaciones.tipoVinculacion =$data.consultaProfesores[i].tipoVinculacion;
            $data.notificaciones.primernombre   = $data.consultaProfesores[i].primernombre;
            $data.notificaciones.segundonombre  =$data.consultaProfesores[i].segundonombre;
            $data.notificaciones.primerapellido =$data.consultaProfesores[i].primerapellido;
            $data.notificaciones.segundoapellido=$data.consultaProfesores[i].segundoapellido;
            $data.notificaciones.correoElectronico=$data.consultaProfesores[i].correoElectronico;

            i=$data.consultaProfesores.length+1;

        }else{
            $data.notificaciones.tipoVinculacion ="";
            $data.notificaciones.primernombre   = "";
            $data.notificaciones.segundonombre  ="";
            $data.notificaciones.primerapellido ="";
            $data.notificaciones.segundoapellido="";
            $data.notificaciones.correoElectronico="";
            $data.notificaciones.fechaContrato   = new Date();
            $data.mostrarCampos.mostrar=true;
        }
    }

    $data.mostrarCampos.mostrar=true;
};

