controller:
function ($scope, $filter, $timeout, $location, $http, Upload, widgetNameFactory) {
    
    this.name = widgetNameFactory.getName('customAdjuntos');
    
    $scope.$watch("properties.targetUrl", function(newValue, oldValue) {
        $scope.preloadFiles();
    });
    
    $scope.$watch("properties.preloadIds", function(newValue, oldValue) {
        $scope.preloadFiles();
    });
    
    $scope.$watch("properties.taskId", function(newValue, oldValue) {
        $scope.init();
    });
    
    $scope.init = function() {
        // Inicializo los selectedFiles
		if(!$scope.selectedFiles) {			
			$scope.selectedFiles = []
		}
		
        if($scope.properties.taskId) {
            $http.get('../API/bpm/task/' + $scope.properties.taskId).then(function(response) {
                if(response.data) {
                    $scope.caseId = response.data.caseId;
                    $scope.activityName = response.data.name;
                    
                    $http.get('../API/bpm/process/' + response.data.processId).then(function(response) {   
                        if(response.data) {
                            $scope.processName = response.data.name;
                            $scope.processVersion = response.data.version;
                        }
                    });
                }
            });
        }
        
        // Cargo archivos que ya esten agregados
		if($scope.properties.selectedFiles) {			
			$http.get($scope.properties.targetUrl + '/findByIds?ids=' + $scope.getIdsString($scope.properties.selectedFiles)).then(function(result) {
				if(result.data) {
				    if($scope.isMobile()){
                        $scope.mobileState.id = result.data;
                        $scope.mobileState.converted = true;
                        $scope.mobileState.uploading = false;
                        $scope.mobileState.progress = 100;
				        
				        $scope.preloadedFiles = result.data;
				    } else {
				        for(var i = 0; i <= result.data.length; i++) {
						var f = result.data[i];
						f.name = f.fileOriginalName;
						f.progress = 100;
						$scope.selectedFiles.push(f);
					}
				    }
				}
			});
		}
    }
    
    //tipo dispositivo
    var vIsMobile = {
    	Android: function() {return navigator.userAgent.match(/Android/i);},
    	BlackBerry: function() {return navigator.userAgent.match(/BlackBerry/i);},
    	iOS: function() {return navigator.userAgent.match(/iPhone|iPad|iPod/i);},
    	Opera: function() {return navigator.userAgent.match(/Opera Mini/i);},
    	Windows: function() {return navigator.userAgent.match(/IEMobile/i);},
    	any: function() {return (vIsMobile.Android() || vIsMobile.BlackBerry() || vIsMobile.iOS() || vIsMobile.Opera() || vIsMobile.Windows());}
    };
    
    $scope.isMobile = function() {
        if(vIsMobile.any() == undefined){
            return false;
        }
        return true;
    }
    
    // Seleccionar los archivos
    $scope.selectFiles = function(files, errFiles) {
        
        if(files.length == 0){
            return;
        }
        
        if(!$scope.selectedFiles) {
            $scope.selectedFiles = new Array();
        }
        
        // validar limite de archivos a subir, y que no este en movil
        if($scope.properties.filesQuantity && !$scope.isMobile()) {
            if($scope.properties.filesQuantity < (files.length + $scope.selectedFiles.length)) {
                alert("Solo puede seleccionar hasta " + $scope.properties.filesQuantity + " archivos");
                return false;
            }
        }
        
        // ---validar dispositivo solo imagenes
        if($scope.isMobile()){
            var errMsj = null;
            //
            if(files.length > 1 || $scope.selectedFiles.length > 0){
                for(var i = 0; i < files.length; i++) {
                    if(!files[i].type.startsWith("image")){
    					isValid = false;
    					errMsj = "Solo puede seleccionar 1 archivo o multiples imagenes.";
    				}
                }
            }
            
            // si hay uno, y no es imagen
            if($scope.selectedFiles.length == 1){
                if(!$scope.selectedFiles[0].type.startsWith("image")){
                    isValid = false;
                    errMsj = "Solo se permite 1 archivo diferente de im치gen";
                }
                
            }
            
            $scope.checkMaxSizeMobile(files);
            
            if(errMsj != null){
                alert(errMsj);
                return false;
            }
        }
        
        for(var i = 0; i < files.length; i++) {
            $scope.selectedFiles.push(files[i]);    
        }
        
        if(errFiles && errFiles.length > 0 && !isMobile()) {
            var errString = "";
            for(var i = 0; i < errFiles.length; i++) {
                if(errFiles[i].$errorMessages.maxSize) {
                    if(errString.length > 0) {
                        errString += "\n";
                    }
                    errString += " - El archivo " + errFiles[i].name + " supera el tama침o permitido de " + errFiles[i].$errorParam
                }
            }
            
            if(errString.length > 0) {
                alert(errString);
            }
        }
    }
    
    // Recupera los archivos del caso
    $scope.preloadFiles = function() {
        if($scope.properties.targetUrl && $scope.properties.preloadIds && $scope.properties.preloadIds.length > 0) {
            $http.get($scope.properties.targetUrl + "/findByIds?ids=" + $scope.getIdsString($scope.properties.preloadIds)).then(function(response) {
                var docs = 	response.data;
				docs.sort(function(a, b) {
					return parseFloat(a.id) - parseFloat(b.id);
				});				
                $scope.preloadedFiles = docs;
            
                //$scope.preloadedFiles = response.data;
                
                if($scope.isMobile()){
                    $scope.mobileState.id = response.data.id;
                    $scope.mobileState.converted = true;
                }
                
            }, function() {
                //alert("Error recuperando los archivos");
            });
        }
    }
    
    $scope.getIdsString = function(idsArray) {
        var idsString = "";
        
        for(var i = 0; i< idsArray.length; i++) {
            if(idsString.length > 0) {
                idsString += ",";
            }
            
            idsString += idsArray[i];
        }
        
        return idsString;
    }
    
    // Subir un archivo
    $scope.upload = function(file) {
        
        // Marco el archivo como en estado subiendo
        file.uploading = true;
        
        if($scope.properties.taskId) {
            file.upload = Upload.upload({
                url: $scope.properties.targetUrl + "/uploadDocument",
                data: {
                    processName: $scope.processName,
                    processVersion: $scope.processVersion,
                    caseId: $scope.caseId,
                    activityName: $scope.activityName,
                    activityId: $scope.properties.taskId,
                    createdBy: $scope.properties.createdBy,
                    identification: $scope.properties.identification,
                    businessCaseId: $scope.properties.businessCaseId,
                    type: $scope.properties.type,
                    file: file
                }
            });    
        } else {
            file.upload = Upload.upload({
                url: $scope.properties.targetUrl + "/uploadDocument",
                data: {
                    createdBy: $scope.properties.createdBy,
                    identification: $scope.properties.identification,
                    businessCaseId: $scope.properties.businessCaseId,
                    type: $scope.properties.type,
                    file: file
                }
            });
        }
        
        file.upload.then(function (response) {
            // Marco el archivo como que termino de subir
            file.uploading = false;
        
            if(!$scope.properties.selectedFiles) {
                $scope.properties.selectedFiles = new Array();
            }
            
            $scope.properties.selectedFiles.push(response.data);
            file.id = response.data;   
            
            $scope.valid = true;
            $scope.dirty = true;
        }, function (response) {
            // Marco el archivo como que termino de subir
            file.uploading = false;
            
            if (response.status > 0) {
                $scope.errorMsg = response.status + ': ' + response.data;
            } else {
                $scope.errorMsg = "Error inesperado, contacte al administrador.";
            }
            
            alert($scope.errorMsg);
            
        }, function (evt) {
            // Marco el archivo como que termino de subir
            file.uploading = false;
            
            file.progress = Math.min(100, parseInt(100.0 * evt.loaded / evt.total));
        });
    }
    
    $scope.mobileState = { 
        uploading : false,
        progress : 0,
        id: null,
        converted : false,
        converting : false,
        maxSize : true
    }

    $scope.uploadMobile = function(){
        $scope.sendMobile($scope.selectedFiles);
    }
    
    $scope.sendMobile = function(file) {
        
        // Marco el archivo como en estado subiendo
        $scope.mobileState.uploading = true;
        $scope.mobileState.progress = 0;
        $scope.mobileState.converted = false;
        $scope.mobileState.converting = true;
        
        if($scope.properties.taskId) {
            file.upload = Upload.upload({
                url: $scope.properties.targetUrl + "/uploadDocumentMobile",
                data: {
                    processName: $scope.processName,
                    processVersion: $scope.processVersion,
                    caseId: $scope.caseId,
                    activityName: $scope.activityName,
                    activityId: $scope.properties.taskId,
                    createdBy: $scope.properties.createdBy,
                    identification: $scope.properties.identification,
                    businessCaseId: $scope.properties.businessCaseId,
                    type: $scope.properties.type,
                    file: file
                },
                arrayKey: ''
            });    
        } else {
            file.upload = Upload.upload({
                url: $scope.properties.targetUrl + "/uploadDocumentMobile",
                data: {
                    createdBy: $scope.properties.createdBy,
                    identification: $scope.properties.identification,
                    businessCaseId: $scope.properties.businessCaseId,
                    type: $scope.properties.type,
                    file: file
                },
                arrayKey: ''
            });
        }
        
        file.upload.then(function (response) {
            // Marco el archivo como que termino de subir
            $scope.mobileState.uploading = false;
        
            if(!$scope.properties.selectedFiles) {
                $scope.properties.selectedFiles = new Array();
            }
            
            $scope.properties.selectedFiles.push(response.data);
            $scope.mobileState.id = response.data;
            $scope.mobileState.converted = true;
            
            // get file info
            $http.get($scope.properties.targetUrl + "/findByIds?ids=" + response.data).then(function(response) {
                $scope.preloadedFiles = response.data;
                $scope.selectedFiles = new Array();
            }, function() {
                //alert("Error recuperando los archivos");
            });
            
            $scope.valid = true;
            $scope.dirty = true;
        }, function (response) {
            // Marco el archivo como que termino de subir
            $scope.mobileState.uploading = false;
            
            if (response.status > 0) {
                $scope.errorMsg = response.status + ': ' + response.data;
            } else {
                $scope.errorMsg = "Error inesperado, contacte al administrador.";
            }
            alert($scope.errorMsg);
            
        }, function (evt) {
            // Marco el archivo como que termino de subir
            $scope.mobileState.progress = Math.min(100, parseInt(100.0 * evt.loaded / evt.total));
            
        });
    }
    
    $scope.abortMobile = function(){
        $scope.selectedFiles.upload.abort();
    }
    
    $scope.checkMaxSizeMobile = function(files){
        var isValid = true;
        if($scope.properties.maxSize){
            var totalBytes = 0;
            
            if(files){
                for(var i = 0; i < files.length; i++) {
                    totalBytes += files[i].size;
                }
            }
            
            for(var i = 0; i < $scope.selectedFiles.length; i++) {
                totalBytes += $scope.selectedFiles[i].size;
            }
            
            if(totalBytes > ($scope.properties.maxSize * 1048576 )){
                isValid = false;
            }
        }
        
        $scope.mobileState.maxSize = isValid;
    }
    
    $scope.removeImagesMobile = function() {
        if($scope.preloadedFiles.length > 0){
        	for(var i = 0; i < $scope.properties.selectedFiles.length; i++) {
                if($scope.properties.selectedFiles[i] == $scope.preloadedFiles[0].id) {
                    $scope.properties.selectedFiles.splice(i, 1);
                    break;
                }
            }
        }
        $scope.preloadedFiles = new Array();
        $scope.mobileState.converted = false;
        $scope.mobileState.progress = 0;
        $scope.mobileState.converting = false;
    }
    
    $scope.removeImageFromList = function(file){
        for(var i = 0; i < $scope.selectedFiles.length; i++) {
            
            if($scope.selectedFiles[i].$$hashKey == file.$$hashKey){
                $scope.selectedFiles.splice(i, 1);
                break;
            }
        }
        
        $scope.checkMaxSizeMobile(null);
    }
    
    $scope.fileCanView = function(file) {
        if(file) {
            var fileType = file.fileContentType ? file.fileContentType : file.type;
            
            return fileType.startsWith("image") || 
                   fileType == "application/pdf" || 
                   fileType == "application/msword" || 
                   fileType == "application/vnd.openxmlformats-officedocument.wordprocessingml.document" ||
                   fileType == "application/vnd.ms-excel" ||
                   fileType == "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet" ||
                   fileType == "application/vnd.ms-powerpoint" ||
                   fileType == "application/vnd.openxmlformats-officedocument.presentationml.presentation";
        } else {
            return false;
        }
    }
    
    // Ver el archivo
    $scope.view = function(file) {
        var url = $scope.properties.targetUrl + "/view/" + file.id;
        window.open(url, "_blank");
    }
    
    // Descargar un archivo
    $scope.download = function(file) {
        var url = $scope.properties.targetUrl + "/download/" + file.id;
        window.location = url;
    }
    
    // Aborta la subida de un archivo
    $scope.abort = function(file) {
        file.upload.abort();
    }
    
    $scope.remove = function(file) {
        for(var i = 0; i < $scope.selectedFiles.length; i++) {
            if($scope.selectedFiles[i].id == file.id) {
                $scope.selectedFiles.splice(i, 1);
                $scope.properties.selectedFiles.splice(i, 1);
                break;
            }
        }
        
        if($scope.properties.selectedFiles.length == 0) {
            $scope.valid = null;
        }
    }
    
    // Retorna el tama침o del archivo en la unidad mas legible
    $scope.getFileSize = function(file) {
        if(file && (file.size || file.fileSize)) {
            
            size = (file.fileSize ? file.fileSize : file.size) / 1024; 
            unit = "KB";
            
            if(size > 1024) { // MB
                size = size / 1024;
                unit = "MB";
            }
            
            return $filter("number")(size, 2) + " " + unit;
        }
        
        return -1;
    }
}
