controller:
/**
 * Widget que verifica si hay peticiones XHR en curso.
 */
function loadingCtrl($scope, $http) {
    $scope.pendientes = 1 ; 
    if($scope.pendientes > 0 ){
        $scope.$watch( function (){ 
            return $http.pendingRequests.length

        } , function() {
            $scope.pendientes = $http.pendingRequests.length;
            $scope.properties.variable = $scope.pendientes === 0;
        });
    }


}
